class Solution:    # @param {string} input an abstract file system    # @return {int} return the length of the longest absolute path to file    def lengthLongestPath(self, input):        max= 0        array= [None]*35        thestr= input.split("\n")        for word in thestr:            counter= word.count("\t")            array[counter]= len(word)- counter            if "." in word:                sum= 0                for num in array[:counter+ 1]:                    sum+= num                if (sum+ counter)> max:                    max= sum+ counter        return max                